#include <iostream>
#include <string>

int getSantaFloor(std::string &input) {
    int sum{};
    int length{};

    while (input[length])
        length++;

    for (int i = 0; i < length; i++) {
        if (sum == -1) {
            std::cout << i << '\n';
        }

        switch(input[i]) {
            case '(':
                sum++;
                break;
            case ')':
                sum--;
                break;
            default:
                break;
        }
    }
    return sum;
}

